				})
			},
			writeArticle(){
				//先判断用户是否登录
				if(this.global.user==null){
					this.$message({
						message: '请先登录',
						type: 'warning'
					});
					return;
				}
				this.$router.push({
					path: '/newsArticle' ,
					// query:{
					// 	course:JSON.stringify(obj)
					// }
				})
			},
			writeSomething(type){
				//先判断用户是否登录
				if(this.global.user==null){
					this.$message({
						message: '请先登录',
						type: 'warning'
					});
					return;
				}
				if(this.writeType == 0){
					this.writeType = type;
				}
				this.writeDialog = true;
				if(this.writeType!=type){
					this.writeContent = '',
					this.writeTitle = "",
					
					//将writeType设置为刚刚点击的type
					this.writeType = type;
				}
				// if(type==1){
				// 	alert("写想法");
				// }else if(type==2){
				// 	alert("提问题");
				// }
			},
			clickNews(obj){
				this.$router.push({
					path: '/newsDetail' ,
					query:{
						newsVo:JSON.stringify(obj)
					}
				})
			},
			//点赞
			like(key,obj){
				if(obj.likeStatus==0){
					obj.likeStatus = 1;
					obj.news.newsLike++;
					this.$set(this.newsList,key,obj);
					
					this.newsLike(obj.news.id);
				}else{
					obj.likeStatus = 0;
					obj.news.newsLike--;
					this.$set(this.newsList,key,obj);
					
					this.newsUnLike(obj.news.id);
				}
				
			},
			newsLike(newsId){
				this.$ajax({
					url: this.global.serverSrc+'/news/newsLike',
					method: 'post',
					params:{
						newsId,
					}
				}).then(res => {
					if(res.data.code==200){
						this.$notify.success({
							title: res.data.message,
						});
						
					}else{
						this.$notify.error({
							title: res.data.message,
						});
					}
				})
			},
			newsUnLike(newsId){
				this.$ajax({
					url: this.global.serverSrc+'/news/newsUnLike',
					method: 'post',
					params:{
						newsId,
					}
				}).then(res => {
					if(res.data.code==200){
						this.$notify.success({
							title: "已取消点赞",
						});
					}
				})
			},
			pubNews(){
				//先判断用户是否登录
				if(this.global.user==null){
					this.$message({
						message: '请先登录',
						type: 'warning'
					});
					return;
				}
				if(this.writeTitle=='' || this.writeContent==''){
					this.$message({
						message: '标题或内容不能为空',
						type: 'warning'
					});
					return;
				}
				this.$ajax({
					url: this.global.serverSrc+'/news/addNews',
					method: 'post',
					params:{
						newsTitle:this.writeTitle,
						newsContent:this.writeContent,
					}
				}).then(res =>{
					console.log(res)
					if(res.data.code==200){
						this.$notify.success(res.data.message);
						//关闭信息框
						this.writeDialog = false;
						//清空输入框
						this.writeTitle = '';
						this.writeContent = '';
						
						//更新news列表
						this.$set(this.newsList,0,res.data.data);
					}
				})
			},
			//评论
			clickNewsComment(obj){
				//先判断用户是否登录
				if(this.global.user==null){
					this.$message({
						message: '请先登录',
						type: 'warning'
					});
					return;
				}
				//跳出评论框
				this.commentDialog = true;
				this.cardObj = obj;
			},
			pubComment(obj){
				//先判断用户是否登录
				if(this.global.user==null){
					this.$message({
						message: '请先登录',
						type: 'warning'
					});
					return;
				}
				if(this.newsComment==''){
					this.$message({
						message: '评论不能为空',
						type: 'warning'
					});
					return;
				}
				this.$ajax({
					url: this.global.serverSrc+'/comment/addComment',
					method: 'post',
					params:{
						entityId:obj.news.id,
						entityType:1,
						commentContent:this.newsComment,
					}
				}).then(res =>{
					if(res.data.code==200){
						this.$notify.success(res.data.message);
						obj.news.newsComment++;
						//关闭评论框
						this.commentDialog = false;
						//清空输入框
						this.newsComment = '';
					}
				})
			},
			load() {
				this.loading = true
				setTimeout(() => {
					this.page ++;
					this.getNews(this.page,this.pageSize);
					this.loading = false
				}, 500)
			},
			getNews(page,pageSize){
				this.$ajax({
					url: this.global.serverSrc+'/news/getNews',
					method: 'post',
					params:{
						page,
						pageSize,
					}
				}).then(res => {
					console.log(res)
					if(res.data.code==200){
						this.newsList = this.newsList.concat(res.data.data.list);
						this.total = res.data.data.pages;
					}
				})
			},
		},
		computed: {
			noMore() {
				return this.page >= this.total
			},
			disabled() {
				return this.loading || this.noMore
			}
		},
		created() {
			this.getNews(this.page,this.pageSize);
		},
		watch:{
			
		}
	}
</script>

